'use strict'

export function test(){
	return new Promise((resolve, reject) => {
		console.log('Make a promise and carry on. ES6 doesn\'t break promises. Eventualy it will get all done...')

		let past 		= new Date().getTime()
			, future 	= past + 10e3 //10 seconds
			, interval = setInterval(() => {
					let now = new Date().getTime()
					
					if (now >= future) {
						clearInterval(interval)
						resolve('Yep! This has been promised and now it is done! :)')
					}
				}, 100)
	}).then(msg => console.log(msg))
}